mapper.select:
/* MembDao.getMembInfo 회원 상세정보 조회 */ select memb_id
,a.memb_pw
,memb_nm ,admin_yn ,use_yn ,join_dt ,regdate ,reguser ,moddate ,moduser ,ifnull(date_format(a.regdate,'%y-%m-%d'),'') as regdate_str ,ifnull(date_format(a.moddate,'%y-%m-%d'),'') as moddate_str from tb_memb_info a where a.memb_id=#{membId} and a.del_yn='N'
and a.memb_pw=#{membPw}

mapper.insert:
/* MembDao.insertMembInfo 회원 정보 등록 */
insert into tb_memb_info (
	memb_id
	,memb_nm
	,memb_pw
	,admin_yn
	,del_yn
	,use_yn
	,join_dt
	,regdate
	,reguser
	,moddate
	,moduser
) values (
	#{membId}
	,#{membNm}
	,#{membPw}
	,ifnull(#{adminYn},'N')
	,ifnull(#{delYn},'Y')
	,ifnull(#{useYn},'N')
	,#{joinDt}
	,now()
	,#{userId}
	,now()
	,#{userId}
)

mapper.update:
/* MembDao.updateMembInfo 회원 정보 수정 */
update tb_memb_info
set
	moddate=now()
	,moduser=#{userId}
	,memb_nm=#{membNm}

	,memb_pw=#{membPw}

	,admin_yn=ifnull(#{adminYn},'N')
where
	memb_id=#{membId}

/* MembDao.deleteMembInfo 회원 정보 삭제 */
update tb_memb_info
set
	del_yn='Y'
where
	memb_id=#{membId}
